{
  "version": "0.2",
  "phases": {
    "build": {
      "commands": [
        "ls && cd AbdulBasit/sprint_3/",
        "pip install -r requirements.txt",
        "npm install -g aws-cdk",
        "cdk synth"
      ]
    }
  },
  "artifacts": {
    "base-directory": "AbdulBasit/sprint_3/cdk.out",
    "files": "**/*"
  }
}
